
public class mahmoud1 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 10;
		int n2 = 20;
		System.out.println(n / n2);
		System.out.println(n % n2);

		double n3 = -10;
		double n4 = -20;

		System.out.println(n3 / n4);
		System.out.println(n3 % n4);

		System.out.println(n4 / n3);
		System.out.println(n4 % n3);

		System.out.println(n4 / n3);
		System.out.println(n4 % n3);
		System.out.println(n3 % n4);

		n3 = 10.5;
		n4 = 5.5;
		System.out.println(n3 % n4);
	}
}
